import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { addMessage, getMessages, getMessagesSuccess } from "../slice/messageReducer.js";

export function* workGetMessages(action){
    const res = yield call(apiCall, "/message/"+action.payload, "get");
    console.log(res.data);
    yield put(getMessagesSuccess(res.data));
}

export function* workAddMessage(action){
    console.log();
    yield call(apiCall, "/message", "post", action.payload.data);
    yield put(getMessages(action.payload.chatId))
}

export function* messageSaga() {
   yield takeLatest(getMessages().type, workGetMessages);
   yield takeLatest(addMessage().type, workAddMessage);
}
