import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { searchUserByPhoneNumber } from '../store/slice/userReducer';
import { getCurrentChat } from '../store/slice/chatReducer';
import { addMessage, getMessages } from '../store/slice/messageReducer';
import './index.scss'

function Home() {
    const [openChat, setOpenChat] = useState(false)
    const dispatch = useDispatch();
    const {searchUser, currentUser} = useSelector(state => state.userReducer);
    const {currentChat} = useSelector(state => state.chatReducer);
    const {messages} = useSelector(state => state.messageReducer);
    function search(){
        let value = document.getElementById("search").value;
        dispatch(searchUserByPhoneNumber(value));
    }

    function sendMessage(){
        console.log(currentUser);
        let value = document.getElementById("send").value;
        dispatch(addMessage({chatId: currentChat.chatId, data: {initiatorId: currentUser.id, chatId: currentChat.chatId, messageBody: value}}))
        dispatch(getMessages(currentChat.chatId));
    }

    function goToChat(){
        dispatch(getCurrentChat({initiatorId: currentUser.id, participantId: searchUser.id}))
        setOpenChat(true);
    }

    return (
        <div className='row'>
            <div className="sider col-4 mr-1">
                <div>
                    <input type="text" id='search' />
                    <button onClick={search} className='btn btn-warning'>search</button>
                </div>
                <div>
                    {
                        searchUser.id ? <div>
                        <h5>{searchUser.fullName}</h5>
                        <p>{searchUser.phoneNumber}</p>
                        <button onClick={()=>goToChat(searchUser.id)}>go to chat</button>
                    </div> : ""
                    }
                </div>
            </div>
            <div className='col-8 '>
                <div className="header border-bottom border-3" >
                    <h1>Header</h1>
                </div>
                <div className='wrapper' style={{height: "400px"}}>
                    {
                        messages.map(message=><div className={message.senderId == currentUser.id ? "sender" : "participant"}>
                            <p className={message.senderId == currentUser.id ? "sender-message" : "part-message"}>{message.messageBody}</p>
                        </div>)
                    }
                </div>
                {
                    openChat ? <div className="footer">
                    <input className='form-control' type="text" id='send' />
                    <button className='btn btn-success my-2'  onClick={sendMessage}>send message</button>
                </div> : ""
                }
            </div>
        </div>
    );
}

export default Home;
