import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getUsers } from './store/slice/userReducer';

function App() {

    const {users} = useSelector(state=>state.userReducer);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getUsers());
        console.log(users);
    }, [])


    return (
        <div>
            <div>
                <select>
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                </select>
                <input type="text" />
                <button>send</button>
            </div>
            <div>
                <div>
                    from: <select name="" id="">
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                    </select>
                    to: <select name="" id="">
                    {
                        users.map(user=> <option>{user.fullName}</option>)
                    }
                    </select>
                    amount: <input type="text" />
                    <button>save</button>
                </div>
                <table>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>fullName</th>
                            <th>phone number</th>
                            <th>money</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user=><tr>
                                <td>{user.id}</td>
                                <td>{user.fullName}</td>
                                <td>{user.phoneNumber}</td>
                                <td>{user.moneyAmount}</td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default App;
