import {call, takeLatest, put, take} from "redux-saga/effects";
import {apiCall} from "../../utils/apiCall.js";
import {getClasses, getCurrentUser, getCurrentUserSuccess, getClassesSuccess} from "../slice/userReducer.js";

export function* workGetCurrentUser(action){
    const res = yield call(apiCall, '/user/token', 'get');
    yield put(getCurrentUserSuccess(res.data))
}

export function* wordGetClasses(action){
    const res = yield call(apiCall, "/lesson", 'get');
    yield put(getClassesSuccess(res.data))
}

export function* userSaga() {
    yield takeLatest(getCurrentUser().type, workGetCurrentUser);
    yield takeLatest(getClasses().type,  wordGetClasses);
}
