import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { searchUserByPhoneNumber, searchUserSuccess } from '../store/slice/userReducer';
import { getChats, getCurrentChat, getCurrentChatSuccess } from '../store/slice/chatReducer';
import { addMessage, getMessages } from '../store/slice/messageReducer';
import './index.scss';
import 'bootstrap-icons/font/bootstrap-icons.css'
import { useNavigate } from 'react-router-dom';
import SockJsClient from 'react-stomp';

function Home() {
    const [openChat, setOpenChat] = useState(false)
    const dispatch = useDispatch();
    const navigate = useNavigate()
    const { searchUser, currentUser } = useSelector(state => state.userReducer);
    const { currentChat, chats } = useSelector(state => state.chatReducer);
    const { messages } = useSelector(state => state.messageReducer);

    useEffect(() => {
        dispatch(getChats(currentUser.id ? currentUser.id : 1))
    }, [])

    function search() {
        let value = document.getElementById("search");
        dispatch(searchUserByPhoneNumber(value.value));
        value.value = "";

    }

    function sendMessage(message) {
        console.log(message);
        let value = document.getElementById("send");
        dispatch(addMessage({ chatId: currentChat.chatId, data: { initiatorId: currentUser.id, chatId: currentChat.chatId, messageBody: value.value } }))
        dispatch(getMessages(currentChat.chatId));
        dispatch(searchUserSuccess({}));
        dispatch(getChats(currentUser.id ? currentUser.id : 1))
        value.value = "";
    }

    function getMessagesByChatId(chat) {
        dispatch(getMessages(chat.chatId));
        dispatch(getCurrentChatSuccess(chat))
        setOpenChat(true);
    }

    function goToChat(id) {
        dispatch(getCurrentChat({ initiatorId: currentUser.id, participantId: id }))
        setOpenChat(true);
    }

    return (
        <div className="container">
            <div className='row'>
                <div className="sider col-4 mr-1">
                    <div className='search'>
                        <input placeholder='Search...' className='search-input' type="text" id='search' />
                        <button onClick={search} className='search-btn'><i className='bi bi-search'></i></button>
                    </div>
                    <div>
                        {
                            searchUser.id ? <div className='chat'>
                                <div>
                                    <h5>{searchUser.fullName}</h5>
                                    <p>{searchUser.phoneNumber}</p>
                                </div>
                                <button onClick={() => goToChat(searchUser.id)}>go to chat</button>
                            </div> : ""
                        }
                        <div style={{ height: "400px" }}>
                            {
                                chats.map(chat => <div onClick={() => getMessagesByChatId(chat)} key={chat.chatId} className={chat.chatId === currentChat.chatId ? " active-chat " : "chat my-1"}>
                                    <div>
                                        <h5>{chat.fullName}</h5>
                                        <p>{chat.phoneNumber}</p>
                                    </div>
                                </div>)
                            }
                        </div>

                        <div className='d-flex justify-content-between'>
                            <div>
                                <img src={"http://localhost:8080/file/" + currentUser.photoUrl} style={{ height: "50px", width: "50px", borderRadius: "50%" }} alt="" />
                                <div>
                                    <h5>{currentUser.fullName}</h5>
                                    <p>{currentUser.phoneNumber}</p>
                                </div>
                            </div>
                            <button style={{height: "45px"}} onClick={()=> navigate("/settings")} className='btn btn-info mt-5'>settings</button>
                        </div>

                    </div>
                </div>
                <div className='col-8 content'>
                    <div className="header border-bottom border-3" >
                        {
                            openChat ? <div className='p-2'>
                                <img width={50} height={50} style={{ borderRadius: "50%" }} src={"http://localhost:8080/file/" + currentChat.photoUrl} alt="" />
                                <div>
                                    <h5>{currentChat.fullName}</h5>
                                    <p>{currentChat.phoneNumber}</p>
                                </div>
                            </div> : ""
                        }
                    </div>
                    <div className='wrapper' style={{ height: "400px" }}>
                        {
                            openChat ? messages.map(message => <div key={message.id} className={message.senderId === currentUser.id ? "sender" : "participant"}>
                                <p className={message.senderId === currentUser.id ? "sender-message" : "part-message"}>{message.messageBody}</p>
                            </div>) : ""
                        }
                    </div>
                    {
                        openChat ? <div className="footer">
                            <div className='foot-wrapper'>
                                <input placeholder='Typing...' className='typing-input' type="text" id='send' />
                                <button className='send-btn' onClick={sendMessage}><i className='bi bi-send'></i></button>
                            </div>
                        </div> : ""
                    }
                </div>
            </div>

            <SockJsClient url='http://localhost:8080/ws' topics={['/topics/all']}
            onMessage={sendMessage}
            onConnect={console.log("connected")}
            />

        </div>
    );
}

export default Home;
