import axios from "axios";


export function apiCall(url, method, data, headers = {"Authorization": localStorage.getItem('access_token')}){
    return axios({
        baseURL: "http://localhost:8080",
        url,
        method,
        data,
        headers
    })
}

export function getAccessToken(){
    setInterval(() => {
        apiCall("/auth/refresh", 'get', null, {refresh_token: localStorage.getItem("refresh_token")}).then(({data})=>{
            localStorage.setItem("access_token", data)
        })
    }, (1000 * 10) - 1000);
}
