import { call, takeLatest, put } from "redux-saga/effects";
import { apiCall } from "../slice/apiCall";
import { getLoans } from "../slice/loanReducer";
import { addLoan } from "../slice/userReducer";

export function* workAddLoan(action){
    yield call(apiCall, "/loan", "post", action.payload.data)
}

export function* loanSaga(){
    yield takeLatest(addLoan().type, workAddLoan);
}
