import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Header from './Header';
import Rodal from 'rodal';
import { takeLesson } from '../store/slice/userReducer';

function Videos(props) {

    const { lessons, les } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const [visible, setVisible] = useState(false);


    function startVideo(lesson){
        dispatch(takeLesson(lesson))
    }

    return (
        <div>
            <Header />
            <div className="body">
                <div className="video-wrapper">
                    {
                        lessons.map(lesson => <div className='video'>
                            <div className='icon'>
                                <i onClick={()=>startVideo(lesson)} className='bi bi-play-circle'></i>
                                <video width={150} src={visible ? "" : "http://localhost:8080/file/" + lesson.video}></video>
                            </div>
                            <div className='title'>
                                <p>{lesson.title}</p>
                            </div>
                        </div>)
                    }
                </div>
            </div>
        </div>
    );
}

export default Videos;
