import React, { useEffect, useState } from 'react';
import { apiCall } from '../utils/apiCall';

function Adminpage() {

    const [advertises, setAdvertises] = useState([]);

    useEffect(()=>{
        getAdminAdvertise();
    }, [])
    
    function getAdminAdvertise(){
        apiCall('/api/lost/admin-page', 'get').then(res=>{
            setAdvertises(res.data);
            console.log(res.data);
        })
    }

    function changeFound(id){
        apiCall("/api/lost/admin/"+id, 'put', {foundStatus: true}).then(res=>{
            console.log('change boldi');
        })
    }

    return (
        <div>
            <div>   
                {
                    advertises?.map(advertise=><div className='card col-4'>
                        <div className="card-header">
                            <h5>{advertise.id}) {advertise.registry}</h5>
                            <p>{advertise.description}</p>
                        </div>
                        <div className="card-body">
                            <div>Buyum yoki Hujjat holati: {advertise.status}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs ismi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs ismi" : ""}: {advertise.firstName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs familyasi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs familyasi" : ""}: {advertise.lastName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs yoshi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs yoshi" : ""}: {advertise.age}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs telefon raqami" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs telefon raqami" : ""}: {advertise.phone_number}</div>
                        </div>
                        <div className="card-footer">
                            <p>Topilgan deb belgilash!</p>
                            <button onClick={()=>changeFound(advertise.id)} className='btn btn-info mx-2'>Ha</button>
                            <button className='btn btn-danger'>Yo'q</button>
                        </div>
                    </div>)
                }
            </div>
        </div>
    );
}

export default Adminpage;
