import React, { useState } from 'react';
import { getCurrentUser } from '../store/slice/userReducer';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { useForm } from 'react-hook-form';
import { apiCall } from '../store/slice/apiCall';
import './login.scss'

function Register() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [imgUrl, setImgUrl] = useState("default.png")
    const { handleSubmit, register, reset } = useForm();

    function send(data) {
        console.log(data);
    }

    function sendFile(e) {
        let formData = new FormData();
        formData.append("file", e.target.files[0]);
        apiCall("/file", "post", formData).then(res => {
            setImgUrl(res.data)
        })
    }
    return (
        <div className='container'>
            <div className='row root'>
                <div className="col-5 wrapper">
                    <form onSubmit={handleSubmit(data => send(data))}>
                        <input id='file' onChange={sendFile} style={{display: "none"}} type="file" />
                        <label className='label' htmlFor="file">
                            <img width={60} height={60} src={"http://localhost:8080/file/" + imgUrl} alt="" />
                        </label>
                        <input placeholder='Full name' className='form-control my-3' type="text" {...register("fullName")} />
                        <input placeholder='Phone number' className='form-control my-3' type="text" {...register("phoneNumber")} />
                        <button className='btn btn-warning w-100'>send</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Register;
