import React, { useEffect, useState } from "react";
import { apiCall } from '../utils/apiCall';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import { useSubscription } from "react-stomp-hooks";
import { Link, Outlet, useNavigate } from "react-router-dom";

function NewMessage() {

    const [users, setUsers] = useState([]);

    useEffect(()=>{
        getChats();
    }, [])

    function saveMessage(e) {

    }

    function getChats(){
        apiCall('/chat/'+localStorage.getItem("userId"), 'get').then(res=>{
            setUsers(res.data);
        })
    }

    function changeInp(e){
        
    }

    function changeSelect(e, user){
        let search = document.getElementById('search');
        search.value = user.firstName;
    }

    return (
        <div className="row">
            <div className="card col-6">
                <form action="">
                    <div className="card-header row">
                        <div className="col-6">
                            <input type="text" id="search" onChange={changeInp} className="form-control my-2" />
                        </div>
                        <div className="col-6">
                            <select name="" onChange={changeSelect} className="form-control my-2" id="select">
                                {
                                    users.map(user=> <option value={user.firstName}>{user.firstName}</option>)
                                }
                            </select>
                        </div>
                    </div>
                    <div className="card-body">
                        <input type="text" className="form-control" />
                    </div>
                    <div className="card-footer">
                        <button className="btn btn-warning w-100">submit</button>
                    </div>
                </form>
            </div>
        </div>
    )
}

export default NewMessage;
