import React, { useEffect, useState } from 'react';
import {apiCall} from '../utils/apiCall';

function Teacher() {

    const [groups, setGroups] = useState([]);
    const [group, setGroup] = useState([]);
    const [timeTable, setTimeTable] = useState([]);
    const [studentTimeTable, setStudentTimeTable] = useState([]);

    useEffect(()=>{
        getGroups();
    }, [])

    function getGroups(){
        apiCall('/group/teacher', 'get').then(res=>{
            setGroups(res.data)
            console.log(res.data);
        })        
    }

    function getGroup(id){
        apiCall('/group/'+id, 'get').then(res=>{
            setGroup(res.data)
            console.log(res.data);
            getTimeTables(id)
        })
    }

    function getTimeTables(groupId){
        apiCall('/time/'+groupId, 'get').then(res=>{
            setTimeTable(res.data)
            console.log(res.data);
        })
    }

    function getStudentTimeTables(timeTableId=2){
        apiCall('/student-time/'+timeTableId, 'get').then(res=>{
            setStudentTimeTable(res.data)
            console.log(res.data);
        })
    }

    // function getTimeTableDays(studentTimeTableId){
    //     apiCall('/day/'+studentTimeTableId, 'get').then(res=>{
    //         console.log(res.data);
    //     })
    // }


    return (
        <div className='container'>
            <div className="row">
                <div className="col-12">
                    <h4 className='text-center'>Dashboard</h4>
                </div>
                <div className="col-1">
                    {
                        groups.map(group=> <div onClick={()=>getGroup(group.id)} className='card my-1'>
                            <div className="card-header">
                                <p>{group.name}</p>
                            </div>
                        </div>)
                    }
                    <div className="card">
                        <div className="card-header">
                            <p className='text-center'><i className='bi bi-gear'></i></p>
                        </div>
                    </div>
                </div>
                <div className="col-4">
                    {
                        group.id ? <div className='card'>
                            <div className="card-header">
                                <h5>{group.name}</h5>
                                <p>price: {group.price}</p>
                                <p>started at: {group.startedat.slice(11)}</p>
                                <p>finished at: {group.finishedat.slice(11)}</p>
                            </div>
                            <div className="card-body">
                                <h5>Students</h5>
                                {
                                    group.students.map(student=> <p>{student.fullName}</p>)
                                }
                            </div>
                        </div> : ''
                    }
                </div>
                <div className="col-7">
                    <div className="col-12 d-flex justify-content-around">
                        {
                            timeTable.map(time=> <div onClick={()=>getStudentTimeTables(time.id)} className='card'>
                                <div className="card-header">{time.name}</div>
                            </div>)
                        }
                    </div>
                    <div className="col-12">
                        {
                            studentTimeTable.map(student=> <div className='d-flex'>
                                <div className='my-2'>{student.id}) {student.fullName}</div>
                                {
                                    student.timeTableDays.map(day=><div className='m-2'>
                                        <select name="" id="">
                                            <option value="0">0</option>
                                            <option value="1">1</option>
                                            <option value="2">2</option>
                                            <option value="3">3</option>
                                            <option value="4">4</option>
                                            <option value="5">5</option>
                                        </select>
                                    </div>)
                                }
                            </div>)
                        }
                    </div>
                </div>
            </div>
            
        </div>
    );
}

export default Teacher;
