import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import { useSubscription } from "react-stomp-hooks";
import { Link, useNavigate } from "react-router-dom";
import Login from "./Login";

function Home() {
    const [users, setUsers] = useState([]);
    const [todos, setTodos] = useState([]);
    const [albums, setAlbums] = useState([]);
    const [photos, setPhotos] = useState([]);
    const navigate = useNavigate();
    useEffect(() => {
        getUsers();
    }, [])


    function getUsers() {
        apiCall('/user', 'get').then(res => {
            setUsers(res.data);
        })
    }

    function getPosts(id) {
        navigate('/post/' + id)
    }
    
    function getTodos(id) {
        navigate('/todo/' + id)
    }


    return (
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <h1 className="text-center">Users</h1>
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>name</th>
                            <th>email</th>
                            <th>username</th>
                            <th>phone</th>
                            <th>actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            users.map(user => <tr key={user.id}>
                                <td>{user.id}</td>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td>{user.username}</td>
                                <td>{user.phone}</td>
                                <td>
                                    <button onClick={() => getPosts(user.id)} className="btn btn-dark m-2">Posts</button>
                                    <button onClick={() => getTodos(user.id)} className="btn btn-warning m-2">Todos</button>
                                    <button onClick={() => getPosts(user.id)} className="btn btn-info m-2">Albums</button>
                                </td>
                            </tr>)
                        }
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default Home;
