import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [currentUser, setCurrentUser] = useState({});
    const [posts, setPosts] = useState([]);
    const [comments, setComments] = useState([]);
    const [visible, setVisible] = useState(false);

    useEffect(() => {
        getCurrentUser();
        getPosts();
    }, [])

    function getCurrentUser() {
        apiCall('/user', 'get', {}, "userId").then(res => {
            setCurrentUser(res.data);
            console.log(res.data);
        })
    }

    function toggle(){
        setVisible(prev=>!prev);
    }

    function getPosts(){
        apiCall('/post', 'get').then(res=>{
            setPosts(res.data);
            console.log(res.data);
        })
    }

    function getComments(index){
        setComments(posts[index].comments)
    }

    return (
        <div className="wrapper row" style={{backgroundColor: "#EBF6FF"}}>
            <div className="row">
            <div className="sider  col-4 border">
                <div style={{ height: "80vh" }}>
                </div>
                {
                    currentUser.id ? <div className="card m-2">
                        <div className="card-header">
                            <h6>{currentUser.firstName + " " + currentUser.lastName}</h6>
                            <p>{currentUser.email}</p>
                        </div>
                    </div> : ""
                }
            </div>
            <div className="content col-8 ">
                {
                    posts.map((post, index)=> <div className="card col-6 my-2">
                        <div className="card-body">
                            <p>{post.content}</p>
                            <p> <span className="profile">{post.fullName[0]}</span> {post.fullName}</p>
                        </div>
                        <div className="card-footer foot">
                            <i style={{color: post.active ? "red" : "", left: "20px"}} className={post.active ? "bi bi-heart-fill" : "bi bi-heart"}></i>
                            <i style={{left: "50px", top: "-11px"}} className="bi bi-chat-dots m-2"></i>
                            <i style={{left: "85px", top: "-8px"}} className="bi bi-send m-2"></i>
                        </div>
                    </div>)
                }
            </div>
            </div>
        </div>
    )
}

export default Home;
