import {createSlice} from "@reduxjs/toolkit";

const state = {
    loans: [],
}

const slice = createSlice({
    name: "loan",
    initialState: state,
    reducers: {
        getLoans(state, action){},
        getLoansSuccess(state, action){
            state.loans = action.payload;
        }
    }
})

export const {} = slice.actions;
export default slice.reducer;
