import 'rodal/lib/rodal.css'

function Home() {

    const [video, setVideo] = useState([]);

    useEffect(()=>{
        getClasses();
        getSciences();
        getLessons();
    }, [])

    function getClasses(){
        apiCall('/lesson', 'get').then(res=>{
            console.log(res.data);
        })
    }

    function getSciences(classNumberId=6){
        apiCall('/lesson/science/'+classNumberId, 'get').then(res=>{
            console.log(res.data);
        })
    }

    function getLessons(scienceId=5){
        apiCall("/lesson/lessons/"+scienceId, 'get').then(res=>{
            console.log(res.data);
            setVideo(res.data)
        })
    }

    return (
        <div>
            salom
            <video width={100} height={100} controls src={"http://localhost:8080/file/" + video[0]?.video}></video>
        </div>
    )
}

export default Home;
