import { apiCall, getAccessToken } from '../utils/apiCall'
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Rodal from 'rodal';
import 'rodal/lib/rodal.css'
import './index.css'

function Home() {

    const [classes, setClasses] = useState([]);

    useEffect(() => {
        getClasses();
        getSciences();
        getLessons();
    }, [])

    function getClasses() {
        apiCall('/lesson', 'get').then(res => {
            setClasses(res.data)
            console.log(res.data);
        })
    }

    function getSciences(classNumberId = 6) {
        apiCall('/lesson/science/' + classNumberId, 'get').then(res => {
            console.log(res.data);
        })
    }

    function getLessons(scienceId = 5) {
        apiCall("/lesson/lessons/" + scienceId, 'get').then(res => {
            console.log(res.data);

        })
    }

    return (
        <div>
            <div className="header">
                <div className="logo">

                </div>
                <div className="navbar">
                    <div className='choose-lang'>
                        <select className='form-control' name="" id="">
                            <option value="uzb">O'zbek</option>
                            <option value="ru">Русский</option>
                            <option value="eng">English</option>
                            <option value="karak">Karakalpak</option>
                        </select>
                    </div>
                    <div className="serach">
                        <input type="text" className='form-control' placeholder=' Qidiruv' />
                    </div>
                </div>
            </div>
            <div className="body">
                <div>
                    <h1 className='text-center'>Videodarsliklar kutubxonasi</h1>
                    <p className='text-center'>Elektron videodarsliklarning eng katta to'plami</p>
                </div>
                <div className='body-wrapper'>
                    {
                        classes.map(clas => <div className='classes'>
                            <div className="card-header">
                                <h2>{clas.className}</h2>
                            </div>
                        </div>)
                    }
                    <div className='classes'>
                        <div className="card-header">
                            <h3>Qoshimcha fanlar</h3>
                        </div>
                    </div>
                    <div className='classes'>
                        <div className="card-header">
                            <h4 className='text-center'>STEAM yo'nalishi bo'yicha video materiallar</h4>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Home;
