import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [announcements, setAnnouncements] = useState([]);
    const [visible, setVisible] = useState(false);
    const [currentUser, setCurrentUser] = useState({});
    const [photoUrl, setPhotoUrl] = useState("default.png")


    useEffect(() => {
        getAnnouncements();
        getCurrentUser();
    }, [])

    function getAnnouncements() {
        apiCall('/announcement', 'get').then(({ data }) => {
            setAnnouncements(data)
            console.log(data);
        })
    }

    function getCurrentUser() {
        apiCall('/user/' + localStorage.getItem("userId"), 'get').then(res => {
            setCurrentUser(res.data);
            console.log(res.data);
        })
    }

    function toggle() {
        setVisible(prev => !prev);
    }

    function addFile(e) {
        let formData = new FormData();
        formData.append("file", e.target.files[0]);
        apiCall("/file", "post", formData).then(res => {
            setPhotoUrl(res.data)
        })
    }

    return (
        <div className="wrapper row" style={{ backgroundColor: "#EBF6FF" }}>
            <div className="row">
                <div className="sider  col-4 border">
                    <div style={{ height: "80vh", paddingTop: "20px" }}>
                        <input id="post" type="text" className="form-control mx-2" placeholder="Content..." />
                        <button className="btn btn-warning w-100 m-2">Add Category +</button>
                    </div>
                    {
                        currentUser.id ? <div className="card m-2">
                            <div className="card-header">
                                <h6>{currentUser.fullName}</h6>
                                <p>{currentUser.email}</p>
                            </div>
                        </div> : ""
                    }
                </div>
                <div className="col-8 ">
                    <h4 className="text-center">All Posts</h4>
                    <div className="content ">

                    </div>
                </div>
            </div>
            <Rodal width={500} height={400} visible={visible} onClose={toggle} >
                <form action="">
                    <label htmlFor="file">
                        <img style={{ borderRadius: "10px", width: "50px", height: "50px" }} src={"http://localhost:8080/file/" + photoUrl} alt="" />
                    </label>
                    <input type="file" onChange={addFile} id="file" style={{ overflow: "hidden" }} />
                    <input type="text" placeholder="" className="form-control my-2" />
                </form>
            </Rodal>
        </div>
    )
}

export default Home;
