import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
function Home() {

    const [announcements, setAnnouncements] = useState([]);
    const [visible, setVisible] = useState(false);
    const [currentUser, setCurrentUser] = useState({});
    const [photoUrl, setPhotoUrl] = useState("default.png");
    const [category, setCategory] = useState([])


    useEffect(() => {
        getAnnouncements();
        getCurrentUser();
        getCategories();
    }, [])

    function getAnnouncements() {
        apiCall('/announcement', 'get').then(({ data }) => {
            setAnnouncements(data)
            console.log(data);
        })
    }

    function getCurrentUser() {
        apiCall('/user/' + localStorage.getItem("userId"), 'get').then(res => {
            setCurrentUser(res.data);
            console.log(res.data);
        })
    }

    function toggle() {
        setVisible(prev => !prev);
    }

    function getCategories(){
        apiCall('/category', 'get').then(res=>{
            setCategory(res.data)
        })
    }

    function addFile(e) {
        let formData = new FormData();
        formData.append("file", e.target.files[0]);
        apiCall("/file", "post", formData).then(res => {
            setPhotoUrl(res.data)
        })
    }

    function addAnnouncement(e){
        e.preventDefault();
        let data = {title: e.target[2].value, description: e.target[3].value, price: e.target[4].value, photoUrl, userId: localStorage.getItem("userId"), categoryId: e.target[1].value}
        console.log(data);
        apiCall('/announcement', 'post', data).then(res=>{
            console.log(res.data);
            getAnnouncements();
        })
    }

    return (
        <div className="wrapper row" style={{ backgroundColor: "#EBF6FF" }}>
            <div className="row">
                <div className="sider  col-4 border">
                    <div style={{ height: "80vh", paddingTop: "20px" }}>
                        <input id="post" type="text" className="form-control mx-2" placeholder="Content..." />
                        <button className="btn btn-warning w-100 m-2">Add Category +</button>
                    </div>
                    {
                        currentUser.id ? <div className="card m-2">
                            <div className="card-header">
                                <h6>{currentUser.fullName}</h6>
                                <p>{currentUser.email}</p>
                            </div>
                        </div> : ""
                    }
                </div>
                <div className="col-8 ">
                    <h4 className="text-center">All Products</h4>
                    <button onClick={toggle} className="btn btn-info my-2">Add Announcement</button>
                    <div className="content ">
                        {
                            announcements.length > 0 ? 
                            announcements.map(announcement=> <div className="card" key={announcement.id}>
                                <div className="card-body d-flex justify-content-around">
                                    <img width={150} height={150} src={"http://localhost:8080/file/"+announcement.photoUrl} alt="" />
                                    <div className=" w-50">
                                    <p>{announcement.title}</p>
                                    <p>{announcement.description}</p>
                                    <p>{announcement.price} so'm</p>
                                    <button className="btn btn-info">contact</button>
                                    </div>
                                </div>
                            </div>) : ""
                        }
                    </div>
                </div>
            </div>
            <Rodal width={500} height={320} visible={visible} onClose={toggle} >
                <form onSubmit={addAnnouncement} action="">
                    <label htmlFor="file">
                        <img style={{ borderRadius: "10px", width: "50px", height: "50px" }} src={"http://localhost:8080/file/" + photoUrl} alt="" />
                    </label>
                    <input type="file" onChange={addFile} id="file" style={{ display: "none" }} />
                    <select className="form-control my-2" name="" id="">
                        {
                            category.map(categoryies=> <option key={categoryies.id} value={categoryies.id}>{categoryies.name}</option>)
                        }
                    </select>
                    <input type="text" placeholder="Title..." className="form-control my-2" />
                    <input type="text" placeholder="Description..." className="form-control my-2" />
                    <input type="text" placeholder="Price..." className="form-control my-2" />
                    <button className="btn btn-warning w-100 my-2">Submit</button>
                </form>
            </Rodal>
        </div>
    )
}

export default Home;
