import React, { useEffect } from 'react';
import Header from '../components/Header';
import Carusel from '../components/Carusel';
import { useState } from 'react';
import { apiCall } from '../utils/apiCall';

function Home() {
    const [statistika, setStatistika] = useState([]);


    useEffect(()=>{
        getStatistika();
    }, [])

    function getStatistika() {
        apiCall('/api/lost/statistika', 'get', null, null).then(res => {
            setStatistika(res.data);
        })
    }

    return (
        <div>
            <Header />
            <div className='statistika'>
                {
                    statistika.map(total=> <div key={total.name} className='card col-4'>
                        <div className="card-body">
                            <p>{total.name}</p>
                            <h6>{total.foundCount} : {total.total}</h6>
                        </div>
                    </div>)
                }
            </div>
            <div>
                <Carusel/>
            </div>
        </div>
    );
}

export default Home;
