import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import Header from './Header';
import Rodal from 'rodal';

function Videos(props) {

    const { lessons } = useSelector(state => state.userReducer);
    const [visible, setVisible] = useState(false);
    const [lesson, setLesson] = useState({});

    function toggle(){
        setVisible(prev=>!prev)
        if(!visible){
            setLesson({});
        }
    }

    function startVideo(lesson){
        setLesson(lesson)
        toggle();
    }

    return (
        <div>
            <Header />
            <div className="body">
                <div className="video-wrapper">
                    {
                        lessons.map(lesson => <div className='video'>
                            <div className='icon'>
                                <i onClick={()=>startVideo(lesson)} className='bi bi-play-circle'></i>
                                <video width={150} src={"http://localhost:8080/file/" + lesson.video}></video>
                            </div>
                            <div className='title'>
                                <p>{lesson.title}</p>
                            </div>
                        </div>)
                    }
                </div>
            </div>
            <Rodal width={1000} height={600} visible={visible} onClose={toggle}>
                    {
                        lesson.id ? <video controls width={900} height={550} src={"http://localhost:8080/file/"+lesson.video}></video> : ""
                    }
            </Rodal>
        </div>
    );
}

export default Videos;
