import {useReducer} from 'react'


const [state, dispatch] = useReducer(reducer, initialState);
const initialState = {
    productAmount: [],
  };
  
 export const reducer = (state, action) => {
    switch (action.type) {
      case 'INCREMENT':
        return { count: state.count + 1 };
      case 'DECREMENT':
        return { count: state.count - 1 };
      default:
        return state;
    }
  };
