import {all} from "redux-saga/effects"
import {userSaga} from "./userSaga";
import { loanSaga } from "./loanSaga";
import { paymentSaga } from "./paymentSaga";


export default function* rootSaga() {
    yield all(
        [
            userSaga(),
            loanSaga(),
            paymentSaga()
        ]
    )
}
