import React, { useEffect, useState } from 'react';
import { apiCall } from '../utils/apiCall';
function Admin() {

    const [operators, setOperators] = useState([]);

    useEffect(()=>{
        getActiveOperators();
    }, [])

    function getActiveOperators(){
        apiCall('/queue/operators', 'get').then(res=>{
            console.log(res.data);
            setOperators(res.data);
        })
    }

    function addOperator(e){
        e.preventDefault();
        let form = e.target;
        let data = {fullName: form[0].value, username: form[1].value, email: form[2].value, password: form[3].value};
        apiCall('/auth/register', 'post', data).then(res=>{
            form[0].value = '';
            form[1].value = '';
            form[2].value = '';
            form[3].value = '';
        })
        
    }

    return (
        <div className='container'>
            <div className="row">
                <div className="col-4 card">
                    <div className="card-header">
                        <h5>Add Operator</h5>
                    </div>
                    <form onSubmit={addOperator} action="">
                        <div className="card-body">
                            <input type="text" className='form-control my-1' />
                            <input type="text" className='form-control my-1' />
                            <input type="text" className='form-control my-1' />
                            <input type="text" className='form-control my-1' />
                        </div>
                        <div className="card-footer">
                            <button className='btn btn-warning w-100'>submit</button>
                        </div>
                    </form>
                </div>
                <div className="col-8">
                    <table className='table'>
                        <thead>
                            <tr>
                                <th>t/r</th>
                                <th>fullName</th>
                                <th>username</th>
                                <th>email</th>
                                <th>status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                operators.map(operator=> <tr>
                                    <td>{operator.id}</td>
                                    <td>{operator.fullName}</td>
                                    <td>{operator.username}</td>
                                    <td>{operator.email}</td>
                                    <td>{operator.status ? "online" : "offline"}</td>
                                </tr>)
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    );
}

export default Admin;
