import React from "react";
import { apiCall } from './utils/apiCall';
import { useNavigate } from "react-router-dom";


function Register() {

    const navigate = useNavigate();

    function register(e){
        e.preventDefault();
        let password = e.target[2].value === e.target[3].value ? e.target[2].value : "";
        let data = {firstName: e.target[0].value, username: e.target[1].value, password}
        if(password){
            apiCall('/auth/register', 'post', data).then(res=>{
                console.log(res.data);
                e.target[0].value = '';
                e.target[1].value = '';
                e.target[2].value = '';
                e.target[3].value = '';
                navigate('/login');
            })
        }else{
            e.target[3].value = 'Ikkala parol mos kelmadi!';
            e.target[3].style.color = 'red'
        }
    }

    return (
        <div className="row">
            <div className="container d-flex justify-content-center">
                <div className="card col-5 mt-5">
                    <form onSubmit={register} action="">
                        <div className="card-header">
                            <h5 className="text-center">Register</h5>
                        </div>
                        <div className="card-body">
                            <input type="text" className="form-control my-2" placeholder="FirstName..." />
                            <input type="text" className="form-control my-2" placeholder="UserName..." />
                            <input type="text" className="form-control my-2" placeholder="Password..." />
                            <input type="text" className="form-control my-2" placeholder="Confirm Password..." />
                        </div>
                        <div className="card-footer">
                            <button className="btn btn-warning w-100">submit</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default Register;
