import React, { useState } from 'react';
import { getCurrentUser } from '../store/slice/userReducer';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { useForm } from 'react-hook-form';
import { apiCall } from '../store/slice/apiCall';

function Login() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [imgUrl, setImgUrl] = useState("http://localhost:8080/file/default.png")
    const { handleSubmit, register, reset } = useForm();

    function send() {
        let value = document.getElementById("input").value;
        dispatch(getCurrentUser({ value, navigate }));
    }

    function sendFile(e){
        let formData = new FormData();
        formData.append("file", e.target.files[0]);
        apiCall("/file", "post", formData).then(res=>{
            setImgUrl(res.data)
        })
    }
    return (
        <div>
            <form onSubmit={handleSubmit(data=>send(data))}>
                <input onChange={sendFile}  type="file"  />
                <img width={50} height={50} src={imgUrl} alt="" />
                <input type="text" {...register("fullName")} />
                <input type="text" {...register("phoneNumber")} />
                <button className='btn btn-warning'>send</button>
            </form>
        </div>
    );
}

export default Login;
