import axios from "axios";


export function apiCall(url, method, data, headers = {"Authorization": localStorage.getItem('access_token')}){
    console.log("nma gap");
    return axios({
        baseURL: "http://localhost:8080",
        url,
        method,
        data,
        headers
    }).catch(err=>{
        console.log(err + "hello world");
        axios({baseURL: "http://localhost:8080/auth/refresh", method: "get", headers: {refresh_token: localStorage.getItem("refresh_token")}}).then(res=>{
            localStorage.setItem("access_token", res.data);
        }).catch()
    })
}
