import { createSlice } from "@reduxjs/toolkit";

const state = {
    categories: [],
    products: [],
    orderProducts: []
}

const slice = createSlice({
    name: "user",
    initialState: state,
    reducers: {
        getCategories(state, action) { },
        getCategoriesSuccess(state, action) {
            state.categories = action.payload;
        },
        getProducts(state, action) { },
        getProductsSuccess(state, action) {
            state.products = action.payload;
        },
        setOrderProduct(state, action) {
            // state.orderProducts.push(action.payload);
            let a = state.orderProducts.filter(order => order.id == action.payload.id);
            if (a.length === 0) {
                state.orderProducts.push({ ...action.payload, count: 1 });
            }
        },
        changeOrderproduct(state, action){
            state.orderProducts = action.payload;
        },
        deleteOrder(state,action){
            state.orderProducts.splice(action.payload, 1)
            console.log(action);
        }
    }
})

export const { getCategories, getCategoriesSuccess, getProducts, getProductsSuccess, changeOrderproduct, setOrderProduct, deleteOrder } = slice.actions
export default slice.reducer
