import { call, takeLatest, put } from "redux-saga/effects";
import { addPost, getPostSuccess, getPosts, deletePost, editPost } from "../slice/PostReducer";
import { apiCall } from "../slice/apiCall";


export function* workGetPosts(action) {
    try {
        const res = yield call(apiCall, "/post/" + action.payload, "get");
        yield put(getPostSuccess(res.data))
        console.log("ishladi");
        console.log(res.data);
    } catch (err) {

    }
}

export function* workAddPost(action) {
    try {
        console.log("action");
        if (!action.payload.data.id) {
            yield call(apiCall, "/post", "post", action.payload.data);
            yield put(getPosts(action.payload.data.userId));
        } else {
            yield call(apiCall, "/post", "put", action.payload.data)
            yield put(getPosts(action.payload.data.userId))
        }
    } catch (err) {

    }
}

export function* workDeletePost(action) {
    try {
        yield call(apiCall, "/post", "delete", action.payload.postId)
        yield put(getPosts(action.payload.userId))
    } catch (err) {

    }
}



export function* postSaga() {
    yield takeLatest(getPosts().type, workGetPosts);
    yield takeLatest(addPost().type, workAddPost);
    yield takeLatest(deletePost().type, workDeletePost);
}
