import React, { useEffect } from 'react';
import Header from './Header';
import { useDispatch, useSelector } from 'react-redux';
import './index.css'
import {getLessons} from '../store/slice/userReducer'
import { useNavigate } from 'react-router-dom';

function Lessons(props) {

    const {sciences} = useSelector(state=>state.userReducer);
    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(()=>{
        console.log(sciences);
    }, [])

    return (
        <div>
            <Header/>
            <div className='body'>
                <div className="wrapper py-3">
                {
                    sciences.map(science=> <div className='card col-3 m-2'>
                        <div className="card-header">
                            <strong className='text-center'>{science.name}</strong>
                        </div>
                        <div className="card-body">
                            <img width={"100%"} src={"http://localhost:8080/file/"+science.photo} alt="" />
                        </div>
                        <div className="card-footer">
                            <button onClick={()=>{dispatch(getLessons(science.id)); navigate('/video')}} className='btn btn-success w-100'>O'tish</button>
                        </div>
                    </div>)
                }
                </div>
            </div>
        </div>
    );
}

export default Lessons;
