import {createSlice} from "@reduxjs/toolkit";
const state = {
    userAdvertise: [],
    currentUser: {}
}


const slice = createSlice({
    name: "user",
    initialState: state,
    reducers: {
        getUserAdvertise(state, action){},
        getUserAdvertiseSuccess(state, action){
            state.userAdvertise = action.payload;
        },
        getCurrentUser(state, action){},
        getCurrentUserSuccess(state,action){
            console.log(action.payload);
            state.currentUser = action.payload;
        }
    }
})

export const {
    getUserAdvertise, 
    getUserAdvertiseSuccess,
    getCurrentUser,
    getCurrentUserSuccess
} = slice.actions
export default slice.reducer
