import React, { useState } from 'react';
import { getCurrentUser } from '../store/slice/userReducer';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { useForm } from 'react-hook-form';
import { apiCall } from '../store/slice/apiCall';
import './login.scss'

function Login() {
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const [imgUrl, setImgUrl] = useState("default.png")
    const { handleSubmit, register, reset } = useForm();

    function send() {
        let value = document.getElementById("input").value;
        dispatch(getCurrentUser({ value, navigate }));
    }

    function sendFile(e) {
        let formData = new FormData();
        formData.append("file", e.target.files[0]);
        apiCall("/file", "post", formData).then(res => {
            console.log(res.data);
            setImgUrl(res.data)
        })
    }
    return (
        <div className='container'>
            <div className='row root'>
                <div className="col-5 wrapper">
                    <form onSubmit={handleSubmit(data => send(data))}>
                        <input  onChange={sendFile} type="file" />
                        <img width={50} height={50} src={"http://localhost:8080/file/" + imgUrl} alt="" />
                        <input className='form-control my-2'  type="text" {...register("fullName")} />
                        <input className='form-control my-2' type="text" {...register("phoneNumber")} />
                        <button  className='btn btn-warning w-100'>send</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
