import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import { useLocation } from "react-router-dom";

function Albums(){
    const [photos, setPhotos] = useState({});
    const [albums, setAlbums] = useState([]);
    const [albumId, setAlbumId] = useState("");
    const location = useLocation();
    useEffect(() => {
        getAlbums(1);
    }, [])
    function getAlbums(page) {
        apiCall('/album/' + location.pathname.slice(7), 'get').then(res => {
            setPhotos(res.data)
        })
    }

    function getPhotos(value, page){
        setAlbumId(value)
        apiCall('/photo/'+value+"?limit=5&page="+page, 'get').then(res=>{
            setPhotos(res.data)
        })
    }

    function generateBtn() {
        let a = photos.pageCount;
        let b = [];
        for (let i = 1; i <= a; i++) {
            b.push(i);
        }
        return (
            <div>
                {
                    b.map(btn => <button className="btn btn-info m-2" onClick={()=>getPhotos(albumId, btn)}>{btn}</button>)
                }
            </div>
        )
    }


    return (
        <div className="container">
            <div className="row">
                <div className="col-12">
                    <h1 className="text-center">Posts</h1>
                    <select name="" id="" onChange={(e)=>getPhotos(e.target.value, 1)}>
                        {
                            albums.map(album=> <option value={album.id}>{album.title}</option>)
                        }
                    </select>
                </div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>title</th>
                            <th>body</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            photos.data ? photos.data.map(photo => <tr>
                                <td>{photo.id}</td>
                                <td>{photo.title}</td>
                                <td>
                                    <img src={photo.url} alt="" />
                                </td>
                            </tr>) : ""
                        }
                    </tbody>
                </table>
                <div className="col-12">
                    {
                        generateBtn()
                    }
                </div>
            </div>
        </div>
    )
}

export default Albums;
