import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { changeContent, getUser } from '../store/slice/userReducer';

function Sider() {

    const {users} = useSelector(state=>state.userReducer);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getUser());
    }, [])


    return (
        <div className='sider'>
            <div className="side-header">
                <h1 className='text-center'>Users</h1>
            </div>
            <div className="map-users">
                {
                    users.map((user, index)=> <div className='user' key={user.id}>
                        <div className='text'>
                            <h5>{user.fullName}</h5>
                            <p>{user.email}</p>
                        </div>
                        <div className='click'>
                            <button onClick={ ()=> dispatch(changeContent({contentType: "posts", userId: user.id}))} className='btn btn-success'>posts</button>
                            <button onClick={ ()=> dispatch(changeContent({contentType: "todos", userId: user.id}))} className='btn btn-success mx-2'>todos</button>
                        </div>
                    </div>)
                }
            </div>
        </div>
    );
}

export default Sider;
