import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage } from '../store/slice/messageReducer';

function Content() {

    const {selectedUser, connectedUser, currentUser} = useSelector(state => state.userReducer);
    const dispatch = useDispatch();
    const {messages} = useSelector(state => state.messageReducer);

    function send(){
        const value = document.getElementById("message").value;
        let data = {chatId: selectedUser.chatId ? selectedUser.chatId : "", initiatorId: currentUser.id, participantId: selectedUser.userId }
        dispatch(addMessage(data))
    }

    return (
        <div className="col-8">
            <div>
                <h1>Messages</h1>
            </div>
            <div>
                <input type="text" id='message' />
                <button className='btn btn-success' onClick={send}>send</button>
            </div>
        </div>
    );
}

export default Content;
