import React from 'react';
import { useSelector } from 'react-redux';
import { apiCall } from '../utils/apiCall';

function Dashboard() {

    const {userAdvertise} = useSelector(state=>state.userReducer);

    function changeFoundStatus(id, foundStatus){
        apiCall("/api/lost/user/"+id, 'put', {foundStatus}).then(res=>{
            console.log('change boldi');
        })
    }

    return (
        <div>
            <div className='row'>
                {
                    userAdvertise?.map(advertise=><div className='card col-4'>
                        <div className="card-header">
                            <h5>{advertise.id}) {advertise.registry}</h5>
                            <p>{advertise.description}</p>
                        </div>
                        <div className="card-body">
                            <div>Buyum yoki Hujjat holati: {advertise.status}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs ismi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs ismi" : ""}: {advertise.firstName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs familyasi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs familyasi" : ""}: {advertise.lastName}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs yoshi" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs yoshi" : ""}: {advertise.age}</div>
                            <div>{advertise.status === "YOQOTIB_QOYILGAN" ? "Topib olgan shaxs telefon raqami" : advertise.status === "TOPIB_OLINGAN" ? "Yo'qotib qoygan shaxs telefon raqami" : ""}: {advertise.phone_number}</div>
                        </div>
                        <div className="card-footer">
                            <p>Ushbu user bo'lishi mumkinmi?</p>
                            <button onClick={()=>changeFoundStatus(advertise.id, true)} className='btn btn-info mx-2'>Ha</button>
                            <button onClick={()=>changeFoundStatus(advertise.id, false)} className='btn btn-danger'>Yo'q</button>
                        </div>
                    </div>)
                }                       
                {/* <table className='table'>
                    <thead>
                        <tr>
                            <th>t/r</th>
                            <th>Registry</th>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Firstname</th>
                            <th>Lastname</th>
                            <th>Age</th>
                            <th>PhoneNumber</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            userAdvertise?.map(advertise=><tr>
                                <td>{advertise.id}</td>
                                <td>{advertise.registry}</td>
                                <td>{advertise.description}</td>
                                <td>{advertise.status}</td>
                                <td>{advertise.firstName}</td>
                                <td>{advertise.lastName}</td>
                                <td>{advertise.age}</td>
                                <td>{advertise.phone_number}</td>
                            </tr>)
                        }
                    </tbody>
                </table> */}
            </div>
        </div>
    );
}

export default Dashboard;
