import React, { useEffect, useState } from "react";
import { apiCall } from './utils/apiCall';
import './index.css';
import Rodal from "rodal";
import 'rodal/lib/rodal.css'
import { useSubscription } from "react-stomp-hooks";
import { Link, useNavigate } from "react-router-dom";
import Login from "./Login";

function Home() {

    const [currentUser, setCurrentUser] = useState({})

    useEffect(()=>{
        getCurrentUser();
    }, [])


    useSubscription("/topic", (msg)=>{
        console.log("salom");
    })

    function getCurrentUser(){
        apiCall('/user/'+localStorage.getItem("userId"), 'get').then(res=>{
            console.log(res.data);
            setCurrentUser(res.data.data)
        })
    }

    return (
        <div className="container">
            <div className="row">
                <div className="sider col-4 border">
                    <h4 className="text-center">Sider</h4>
                    <div className="card m-1">
                        <div className="car-header">Send Messages: {currentUser.sendMessagesCount}</div>
                    </div>
                    <div className="card m-1">
                        <div className="car-header">Inbox: {currentUser.inInboxMessagesCount}</div>
                    </div>
                    <div className="card m-1">
                        <div className="car-header">Drafts: {currentUser.draftMessagesCount}</div>
                    </div>
                    <div className="div settings">
                        <h6>{currentUser.firstName + " " + currentUser.lastName}</h6>
                        <p>@{currentUser.email}</p>
                    </div>
                </div>
                <div className="content col-8 border">

                </div>
            </div>
        </div>
    )
}

export default Home;
