import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getPosts, toggle } from '../store/slice/PostReducer';
import Rodal from 'rodal';

function Posts() {

    const { posts, visible } = useSelector(state => state.postReducer);
    const { currentUserId } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getPosts(currentUserId));
    }, [currentUserId])

    return (
        <div >
            <div className='row'>
                {
                    posts.map((post, index) => <div className='card col-3 my-1 mx-4' key={post.id}>
                        <div className="card-header">
                            <h5 className='text-center'>{post.title}</h5>
                        </div>
                        <div className="card-body">
                            {post.body}
                        </div>
                        <div className="card-footer">
                            <button className="btn btn-success mx-1">edit</button>
                            <button className="btn btn-danger mx-1">delete</button>
                        </div>
                    </div>)
                }
            </div>
            <div>
                <button onClick={()=>dispatch(toggle())} className='btn btn-warning mx-3 my-4'>add +</button>
            </div>

            <Rodal visible={visible} onClose={dispatch(toggle)}>

            </Rodal>
        </div>
    );
}

export default Posts;
