import { useNavigate } from 'react-router-dom';
import { apiCall } from '../utils/apiCall'
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getCurrentUser, getUserAdvertise } from '../store/slice/userReducer';

function Login() {

    const navigate = useNavigate();
    const { userAdvertise, currentUser } = useSelector(state => state.userReducer);
    const dispatch = useDispatch();

    function submit(e) {
        e.preventDefault();
        let data = { username: e.target[0].value, password: e.target[1].value }
        apiCall('/auth/login', 'post', data, {}).then(res => {
            dispatch(getCurrentUser({navigate}));
            dispatch(getUserAdvertise());
            localStorage.setItem("access_token", res.data.access_token);
            localStorage.setItem("refresh_token", res.data.refresh_token);
            navigate('/')
        }).catch(err=>{
            alert("login yoki parol xato")
        })
    }

    return (
        <div className='container'>
            <div className="row d-flex justify-content-center">
                <div className="card col-5">
                    <form onSubmit={submit} action="">
                        <div className="card-header">
                            <h5 className='text-center'>Login</h5>
                        </div>
                        <div className="card-body">
                            <input type="text" className='form-control my-2' placeholder='Username...' />
                            <input type="text" className='form-control my-2' placeholder='Password...' />
                        </div>
                        <div className="card-footer">
                            <button className='btn btn-warning w-100'>submit</button>
                            <a href="http://localhost:3000/register">register</a>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
