import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import 'bootstrap/dist/css/bootstrap.min.css';
import './register.scss'

function Register() {

    const { reset, register, handleSubmit, formState: { errors }, watch, ref } = useForm({
        defaultValues: {
            firstName: "",
            lastName: "",
            email: "",
            password: ""
        }
    });


    function submit(data) {
        console.log(data);
    }


    return (
        <div className="wrapper">
            <div className='container register'>
                <div className="row d-flex justify-content-center align-items-center h-100">
                    <div className='col-5 card'>
                        <div className="card-header">
                            <h4 className='text-center'>Register</h4>
                        </div>
                        <div className="card-body">
                            <form onSubmit={handleSubmit((data) => submit(data))} id='register'>
                                First Name
                                <div className='wrap-inp'>
                                    <input {...register('firstName', { required: "This is Required !" })} type="text" className="form-control" />
                                    <p className='error'> {errors.firstName?.message} </p>
                                </div>
                                Last Name
                                <div className="wrap-inp">
                                    <input {...register('lastName', { required: "This is Required !" })} type="text" className="form-control" />
                                    <p className="error">{errors.lastName?.message}</p>
                                </div>
                                Email
                                <div className="wrap-inp">
                                    <input
                                        placeholder="Email"
                                        {...register('email', {
                                            required: 'Email is required',
                                            pattern: {
                                                value: /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/,
                                                message: 'Please enter a valid email',
                                            },
                                        })}
                                        type="email"
                                        required
                                        className="input"
                                    />
                                    <p className="error">{errors.email?.message}</p>
                                </div>
                                Password
                                <div className="wrap-inp">
                                    <input {...register('password')} type="text" className="form-control" />
                                    <p className="error"></p>
                                </div>
                            </form>
                        </div>
                        <div className='card-footer'>
                            <button form='register' type='submit' className="btn btn-warning my-2 w-100"> Send </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Register;
