import React, { useEffect } from 'react';

const WebSocketComponent = () => {
  useEffect(() => {
    const socket = new WebSocket('ws://localhost:8080/ws');

    // Ulanish muvaffaqiyatli amalga oshirildi
    socket.onopen = () => {
      console.log('Ulanish muvaffaqiyatli amalga oshirildi');
    };

    // Ulanishdan kelgan xabarlar
    socket.onmessage = (event) => {
      console.log('Xabar olindi:', event.data);
      // Xabar kelganda kerakli amallarni bajarish
    };

    // Ulanish yopilganda
    socket.onclose = () => {
      console.log('Ulanish yopildi');
      // Ulanishni qayta tiklash uchun kerakli amallarni bajarish
    };

    // Ulanishda xato yuz berish
    socket.onerror = (error) => {
      console.error('Ulanishda xato yuz berdi:', error);
      // Xato yuz berishda kerakli amallarni bajarish
    };

    // Komponent bekor qilindiğida ulanishni yopish
    return () => {
      socket.close();
    };
  }, []);

  // Komponent JSX-i
  return <div>WebSocket ulanish kodlari</div>;
};

export default WebSocketComponent;
