import {call, takeLatest, put} from "redux-saga/effects";
import {apiCall} from "../slice/apiCall.js";
import { addInCome, getUsers, getUsersSuccess } from "../slice/userReducer.js";


export function* workGetUsers(action){
    const res = yield call(apiCall, "/user", "get");
    yield put(getUsersSuccess(res.data));
    console.log(res);
}

export function* workAddInCome(action){
    yield call(apiCall, "/user/in-come", "post", action.payload);
    yield put(getUsers())
}

export function* workAddExpanse(action){
    yield call(apiCall, "/user/expanse", "post", action.payload)
}

export function* userSaga() {
    yield takeLatest(getUsers().type, workGetUsers);
    yield takeLatest(addInCome().type, workAddInCome);
}
